import { Component, Input, OnInit }	from '@angular/core';

import { CommentService } from './comment.service';

@Component({
	selector: 'comment',
	template: `
<comment-form [parent]="" (commentAdded)="commentListing.refresh($event)"></comment-form>
<comment-listing #commentListing></comment-listing>
	`
})
export class CommentComponent {
	@Input() config: any;

	constructor(private commentService: CommentService) {}

	ngOnInit() {
		this.commentService.config = this.config;
	}
}